CREATE view [dbo].[vw_BankaHesap]
as
select 
t.TanimAdi as BankaAdi,
t1.TanimAdi as ParaBirimi,
(isnull((select sum(isnull(bhh1.Tutar,0)) from BankaHesapHareket bhh1 where bhh1.BankaHareketTipi=1 and bhh1.BankaHesapID=bh.BankaHesapID),0)
-
isnull((select sum(isnull(bhh1.Tutar,0)) from BankaHesapHareket bhh1 where bhh1.BankaHareketTipi=2 and bhh1.BankaHesapID=bh.BankaHesapID),0)) as Bakiye,
bh.*
from BankaHesap bh 
left outer join Tanim t on (t.TanimID=bh.BankaID)
left outer join Tanim t1 on (t1.TanimID=bh.ParaTuruID)




