CREATE view [dbo].[vw_BankaHesapHareket]
as
select 
t.TanimAdi as BankaAdi,
bh.HesapKodu,bh.HesapNo,bh.IBAN,bh.SubeKodu,bh.SubeAdi,
sbt.SabitAdi as BankaHareket,
sbt2.SabitAdi as OdemeSekli,
t2.TanimAdi as ParaTuru,
t3.TanimAdi as EvrakTuru,
c.CariAdi,
iy.IsyeriAdi,
kul.KullaniciAdi+' '+kul.KullaniciSoyadi as KullaniciAdiSoyadi,
bh.KrediKarti,bh.POSVarmi,
bhh.*
from BankaHesapHareket bhh
inner join BankaHesap bh on (bhh.BankaHesapID=bh.BankaHesapID)
left outer join Tanim t on (t.TanimID=bh.BankaID)
left outer join Sabit sbt on (sbt.SabitID=bhh.BankaHareketTipi and sbt.Tur='BANKA')
left outer join Sabit sbt2 on (sbt2.SabitID=bhh.OdemeSekliID and sbt2.Tur='ODEME_SEKLI')
left outer join Tanim t2 on (t2.TanimID=bh.ParaTuruID)
left outer join Cari c on (c.CariID=bhh.CariID)
left outer join Isyeri iy on (iy.IsyeriID=bh.IsyeriID)
left outer join Kullanici kul on (kul.KullaniciID=bh.KullaniciID)
left outer join Tanim t3 on (t3.TanimID=bhH.EvrakTuruID)

