CREATE view [dbo].[vw_CariBakiye]
as 
select 
c.CariID,
c.CariAdi,
t.TanimID as CariGrupID,
t.TanimAdi as CariGrupAdi,
sum(isnull(ch.Alacak,0))-isnull((select sum(isnull(ch1.Alacak,0)) from CariHareket ch1 left outer join Fatura f1 on (f1.FaturaID=ch1.FaturaID) where cast(isnull(f1.Iade,0) as bit)=1 and c.CariID=ch1.CariID),0) as Alacak,
sum(isnull(ch.Borc,0))-isnull((select sum(isnull(ch1.Borc,0)) from CariHareket ch1 left outer join Fatura f1 on (f1.FaturaID=ch1.FaturaID) where cast(isnull(f1.Iade,0) as bit)=1 and c.CariID=ch1.CariID),0) as Borc,

(sum(isnull(ch.Borc,0))-isnull((select sum(isnull(ch1.Borc,0)) from CariHareket ch1 left outer join Fatura f1 on (f1.FaturaID=ch1.FaturaID) where cast(isnull(f1.Iade,0) as bit)=1 and c.CariID=ch1.CariID),0))
-
(sum(isnull(ch.Alacak,0))-isnull((select sum(isnull(ch1.Alacak,0)) from CariHareket ch1 left outer join Fatura f1 on (f1.FaturaID=ch1.FaturaID) where cast(isnull(f1.Iade,0) as bit)=1 and c.CariID=ch1.CariID),0)) as Bakiye,

--(sum(isnull(ch.Borc,0))-sum(isnull(ch.Alacak,0))) as Bakiye,
(case when ((sum(isnull(ch.Borc,0))-isnull((select sum(isnull(ch1.Borc,0)) from CariHareket ch1 left outer join Fatura f1 on (f1.FaturaID=ch1.FaturaID) where cast(isnull(f1.Iade,0) as bit)=1 and c.CariID=ch1.CariID),0))-(sum(isnull(ch.Alacak,0))-isnull((select sum(isnull(ch1.Alacak,0)) from CariHareket ch1 left outer join Fatura f1 on (f1.FaturaID=ch1.FaturaID) where cast(isnull(f1.Iade,0) as bit)=1 and c.CariID=ch1.CariID),0)))=0 then 'Alacak veya Borç Bulunmamaktadır'
        when ((sum(isnull(ch.Borc,0))-isnull((select sum(isnull(ch1.Borc,0)) from CariHareket ch1 left outer join Fatura f1 on (f1.FaturaID=ch1.FaturaID) where cast(isnull(f1.Iade,0) as bit)=1 and c.CariID=ch1.CariID),0))-(sum(isnull(ch.Alacak,0))-isnull((select sum(isnull(ch1.Alacak,0)) from CariHareket ch1 left outer join Fatura f1 on (f1.FaturaID=ch1.FaturaID) where cast(isnull(f1.Iade,0) as bit)=1 and c.CariID=ch1.CariID),0)))>0 then 'Cari Borçlu' 
	    else 'Cari Alacaklı' end) as BakiyeDurumu
from Cari c
left outer join CariHareket ch on (ch.CariID=c.CariID)
left outer join Fatura f on (f.FaturaID=ch.FaturaID)
left outer join Tanim t on (t.TanimID=c.CariGrupID)
where cast(isnull(f.Iade,0) as bit)=0
group by c.CariID,c.CariAdi,t.TanimAdi,t.TanimID







