CREATE view [dbo].[vw_Fatura]
as 
select
'' as Sira, 
sbt.SabitAdi as FaturaTipiAdi,
k.KullaniciAdi+' '+k.KullaniciSoyadi as SatisEleman,
t.TanimAdi as Depo,
k1.KullaniciAdi+' '+k1.KullaniciSoyadi as KullaniciAdiSoyadi,
