f.*
from Fatura f
left outer join Sabit sbt on (sbt.SabitID=f.FaturaTipi and (sbt.Tur='FATURA' or sbt.Tur='IRSALIYE'))
left outer join Kullanici k on (k.KullaniciID=f.SatisElemanID)
left outer join Tanim t on (t.TanimID=f.DepoID)
