CREATE view [dbo].[vw_StokListesi] as
select 
sg.TanimAdi as StokGrubuAdi,
sg.N11Kodu as StokGrubuN11Kodu,
sb.TanimAdi as StokBirimAdi,
akd.SabitAdi as AlisKdvDurumAdi,skd.SabitAdi as SatisKdvDurumAdi,
kul.KullaniciAdi+' '+kul.KullaniciSoyadi as KullaniciAdiSoyadi,
cast(ROUND(isnull(sd.MevcutStokAdeti,0)*isnull(stk.AlisFiyati,0)*pb.AlisDegeri,2) as decimal(18,2)) as ToplamAlis,
cast(ROUND(isnull(sd.MevcutStokAdeti,0)*isnull(stk.SatisFiyati,0)*pb.SatisDegeri,2) as decimal(18,2)) as ToplamSatis,
depo.TanimAdi as Depo,
sd.DepoID,
sd.KritikStokAdeti as DepoKritikStokAdeti,
sd.GirenAdet,sd.CikanAdet,sd.MevcutStokAdeti as DepoMevcutStokAdeti,
round(isnull(stk.AlisFiyati,0)*isnull(sd.MevcutStokAdeti,0),2) as ToplamVarlik,
asg.TanimAdi as AltStokGrubuAdi,
asg2.TanimAdi as AltStokGrubuAdi2,
asg3.TanimAdi as AltStokGrubuAdi3,
asg.N11Kodu as AltStokGrubuN11Kodu,
asg2.N11Kodu as AltStokGrubuN11Kodu2,
asg3.N11Kodu as AltStokGrubuN11Kodu3,
mrk.MarkaAdi as Marka,
cins.TanimAdi as UrunCinsiyet,
mal.TanimAdi as UrunMalzeme,
pb.Kodu as ParaBirimiKodu,
(select count(sr.StokID) from StokResim sr where sr.StokID=stk.StokID) as ResimGaleriSayisi,
stk.* 
from Stok stk
left outer join Tanim sg on (sg.TanimID=stk.StokGrubuID)
left outer join Tanim asg on (asg.TanimID=stk.AltStokGrubID)
left outer join Tanim asg2 on (asg2.TanimID=stk.AltStokGrubID2)
