CREATE view [dbo].[vw_StokVaryantListesi] as
select
sd.StokDepoID,
(case when stkv.BarkodNo is null then stk.BarkodNo else stkv.BarkodNo end) as VaryantBarkodNo,
sg.TanimAdi as StokGrubuAdi,sb.TanimAdi as StokBirimAdi,
akd.SabitAdi as AlisKdvDurumAdi,skd.SabitAdi as SatisKdvDurumAdi,
kul.KullaniciAdi+' '+kul.KullaniciSoyadi as KullaniciAdiSoyadi,
ROUND(isnull(sd.MevcutStokAdeti,0)*isnull(stk.AlisFiyati,0),2) as ToplamAlis,
ROUND(isnull(sd.MevcutStokAdeti,0)*isnull(stk.SatisFiyati,0),2) as ToplamSatis,
depo.TanimAdi as Depo,
sd.IsyeriID as DepoIsyeriID,
sd.DepoID,
sd.StokVaryantID,
sd.KritikStokAdeti as DepoKritikStokAdeti,
sd.GirenAdet,sd.CikanAdet,sd.MevcutStokAdeti as DepoMevcutStokAdeti,
round(isnull(stk.AlisFiyati,0)*isnull(sd.MevcutStokAdeti,0),2) as ToplamVarlik,
asg.TanimAdi as AltStokGrubuAdi,
asg2.TanimAdi as AltStokGrubuAdi2,
asg3.TanimAdi as AltStokGrubuAdi3,
r.TanimID as RenkTanimID,
r.TanimAdi as Renk,
r.N11Kodu as RenkN11Kodu,
n.TanimID as NumaraTanimID,
n.TanimAdi as Numara,
n.N11Kodu as NumaraN11Kodu,
u.TanimID as UzunlukTanimID,
u.TanimAdi as Uzunluk,
u.N11Kodu as UzunlukN11Kodu,
b.TanimID as BedenTanimID,
b.TanimAdi as Beden,
b.N11Kodu as BedenN11Kodu,
mrk.MarkaAdi as Marka,
cins.TanimAdi as UrunCinsiyet,
mal.TanimAdi as UrunMalzeme,
 substring(((case when stkv.RenkID>0 then +'-'+isnull(r.TanimAdi,'')  else '' end)+
 (case when stkv.NumaraID>0 then +'-'+isnull(n.TanimAdi,'')  else '' end)+
 (case when stkv.UzunlukID>0 then +'-'+isnull(u.TanimAdi,'') else '' end)+
 (case when stkv.BedenID>0 then +'-'+isnull(b.TanimAdi,'') else '' end)),2,8000) as VaryantAdi,

(isnull(stk.StokAdi,'')+' '+
 substring(((case when stkv.RenkID>0 then +'-'+isnull(r.TanimAdi,'')  else '' end)+
 (case when stkv.NumaraID>0 then +'-'+isnull(n.TanimAdi,'')  else '' end)+
 (case when stkv.UzunlukID>0 then +'-'+isnull(u.TanimAdi,'') else '' end)+
 (case when stkv.BedenID>0 then +'-'+isnull(b.TanimAdi,'') else '' end)),2,8000)) as StokVaryantAdi,
 pb.TanimAdi as ParaBirimi,
 pb.Sembol as ParaSembolu,
 pb.Kodu as ParaBirimiKodu,
stk.* 
from StokDepo sd
left outer join Stok stk on (sd.StokID=stk.StokID)
left outer join StokVaryant stkv on (stkv.StokVaryantID=sd.StokVaryantID)
