	fd.KdvTutar,fd.AraToplam,fd.Toplam,f.ToplamTutar,f.GenelToplam,
	ch.EvrakNo as CariHareketEvrakNo, 
	c.CariAdi,sbt1.SabitAdi as OdemeSekli,
	c.YetkiliAdi,c.YetkiliSoyadi,c.Telefon,c.Faks,EMail,
	sbt.SabitAdi as CariIslemTipi,
	convert(nvarchar,ch.IslemTarihi,104) as IslemTarihi1,
	convert(nvarchar,ch.VadeTarihi,104) as VadeTarihi1,
	ch.* 
from CariHareket ch 
	left outer join Sabit sbt1 on (sbt1.SabitID=ch.OdemeSekliID)
	left outer join Cari c on (c.CariID=ch.CariId)  
	left outer join Sabit sbt on (sbt.SabitID=ch.CariIslemTuru and sbt.Tur='CARI')  
	left outer join Fatura f on (f.FaturaID=ch.FaturaID)
	left outer join FaturaDetay fd on (f.FaturaID=fd.FaturaID)
