CREATE proc [dbo].[sp_KasaListele]
(
 @TarihBaslama date=null,
 @TarihBitis   date=null,
 @KasaTipiId   int=0,
 @EvrakTuruId  int=0
) 
as
begin
  Declare @SqlText nvarchar(4000)  
  
set @SqlText=' select s.SabitAdi as KasaTipi,rtrim(ltrim(isnull(c.CariAdi,'''')+'' ''+isnull(cbhh.CariAdi,''''))) as CariAdi,
     convert(nvarchar,Tarih,104) as Tarih,
     t.TanimAdi as EvrakTuru,kul.KullaniciAdi+'' ''+kul.KullaniciSoyadi as Kullanici,
     (case when k.KasaTipiID=1 then k.Tutar else 0 end) as GirisTutar, 
     (case when k.KasaTipiID=2 then k.Tutar else 0 end) as CikisTutar, 
     k.*  
     from   Kasa k 
     left outer join Sabit s on (s.SabitID=k.KasaTipiID and s.Tur=''KASA'') 
     left outer join Tanim t on (t.TanimID=k.EvrakTuruID) 
     left outer join Kullanici kul on (kul.KullaniciID=k.KullaniciID)
     left outer join CariHareket ch on (ch.CariHareketID=k.CariHareketID)
     left outer join Cari c on (c.CariID=ch.CariID)
	 left outer join BankaHesapHareket bhh on (bhh.BankaHesapHareketID=k.BankaHesapHareketID)
	 left outer join Cari cbhh on (cbhh.CariID=bhh.CariID)
     where 
     k.Tarih between '''+convert(nvarchar,@TarihBaslama,102)+''' and '''+convert(nvarchar,@TarihBitis,102)+''' '
     
if(@EvrakTuruId!=0)
set @SqlText+=' and k.EvrakTuruID='+cast(@EvrakTuruId as nvarchar)+' '

if(@KasaTipiId!=0)
set @SqlText+=' and k.KasaTipiID='+cast(@KasaTipiId as nvarchar)+' '

set @SqlText+=' order by k.KayitZamani Desc '

exec(@SqlText)
     
end

