/****** Object:  StoredProcedure [dbo].[sp_MaasListesi]    Script Date: 1.11.2018 17:29:29 ******/
SET ANSI_NULLS ON
GO
SET QUOTED_IDENTIFIER ON
GO
CREATE proc [dbo].[sp_MaasListesi]
(
 @PersonelId int=0,
 @Ay tinyint=0,
 @Yil smallint=0
) as 
   select 
	per.PersonelAdi,per.PersonelSoyadi,
	per.BankaHesapNo,per.BankaIbanNo,
	t.TanimAdi as Kurumu,t1.TanimAdi as Departmani,
	(select isnull(m1.MaasTutari,0) from Maas m1 where m1.PersonelID=m.PersonelId and m1.ay=m.ay and m1.Yil=m.Yil) as MaasTutari,
	(select isnull(m1.BankayaYatanTutar,0) from Maas m1 where m1.PersonelID=m.PersonelId and m1.ay=m.ay and m1.Yil=m.Yil) as BankayaYatanTutar,
	SUM(ISNULL(mh.IslemTutari,0)) as OdenenTutar,

	((select isnull(m1.MaasTutari,0) from Maas m1 where m1.PersonelID=m.PersonelId and m1.ay=m.ay and m1.Yil=m.Yil)-
	SUM(ISNULL(mh.IslemTutari,0))+
	(select isnull(m1.BankayaYatanTutar,0) from Maas m1 where m1.PersonelID=m.PersonelId and m1.ay=m.ay and m1.Yil=m.Yil)) as NetTutar,
	
	ISNULL((select SUM(isnull(mh1.IslemTutari,0)) from MaasHareket mh1 where mh1.IslemTipiID=46 and mh1.PersonelID=m.PersonelID and mh1.ay=m.ay and mh1.Yil=m.Yil),0) as AvansTutari

	from Personel per
	left outer join Maas m on (m.PersonelID=per.PersonelID)
	left outer join MaasHareket mh on (mh.MaasId=m.MaasID)
	left outer join Tanim t on (t.TanimID=per.KurumID)
	left outer join Tanim t1 on (t1.TanimID=per.DepartmanID)
	where m.Ay=@Ay and m.Yil=@Yil and per.Aktif=1 
	group by per.PersonelAdi,per.PersonelSoyadi,m.Ay,m.Yil,m.PersonelID,
	per.BankaHesapNo,per.BankaIbanNo,
	t.TanimAdi,t1.TanimAdi
	order by per.PersonelAdi,per.PersonelSoyadi
GO
